import List from "./List";
import data from "./data";
import { useState } from "react";

function App() {
  const [people, setPeople] = useState(data);

  const removeItem = (id) => {
    setPeople((oldValue) => oldValue.filter((value) => value.id !== id));
  };

  const reloadAllItem = () => {
    setPeople(data);
  };

  return (
    <>
    <section>
      <div className="container">
        <h2 style={{ color: "var(--bg-blue)" }}>To do list</h2>
        <div className="people-list">
          <List data={people} removeItem={removeItem} />
        </div>
        <div className="btn-group">
          <button className="btn btn-reset" onClick={reloadAllItem}>
            {" "}
            Reload{" "}
          </button>

          <button className="btn btn-delete" onClick={() => setPeople([])}>
            {" "}
            Delete all
          </button>
        </div>
      </div>
      <div className="container">
      <h2 style={{ color: "var(--bg-blue)" }}>To do list</h2>
      <form action="">
      <label htmlFor="taskTitle">Task title</label>
      <input type="text" name="taskTitle" id="taskTitle" />
        <br />
        <label htmlFor="taskDescription"></label>
        <input type="text" name="taskDescription" id="taskDescription" />
        <br />
        <button className="btn">Add task</button>
      </form>
    </div>
    </section>
  </>
  );
}

export default App;
