import React from "react";


const FormTask = (props) => {
    let newItem = {
        id: 6,
        title: "Mail urgente",
        description: "Inviare mail urgente al capo",
    };
return (
    <>
        <form action="">
            <div className="row">
                <label htmlFor="taskTitle">Task title</label>
                <input type="text" name="taskTitle" id="taskTitle" />
            </div>
            <div className="row">
                <label htmlFor="taskDescription">Task description</label>
                <input type="text" name="taskDescription" id="taskDescription" />
            </div>
            <button className="btn" onClick={() => props.insertItem(newItem)}>{"Add task"}</button>
        </form>
    </>
);
};

export default FormTask;
