import React, { useState } from "react";


const FormTask = (props) => {
    const [titleInput, setTitleInput] = useState('');
    const [descriptionInput, setDescriptionInput] = useState('');

    const handleChange = e => {
        setTitleInput(e.target.value);
    };

    const handleSubmit = e => {
        e.preventDefault();
        props.onSubmit(
            {
                id: Math.floor(Math.random()*10000),
                title: titleInput,
                description: descriptionInput,
            }
        );
        setTitleInput('');
    }


    return (
        <>
            <form className="form-task" onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="taskTitle">Task title</label>
                    <input type="text" placehonder="Add to do title" name="taskTitle" id="taskTitle" value={titleInput} onChange={handleChange}/>
                </div>
                <div>
                    <label htmlFor="taskDescription">Task description</label>
                    <input type="text" placehonder="Add to do description" name="taskDescription" id="taskDescription" value={descriptionInput} />
                </div>
                <button className="btn" onClick={() => insertItem({
                    id: 6,
                    title: "Mail urgente",
                    description: "Inviare urgente al capo",
                })}>{"Add task"}</button>
            </form>
        </>
    );
};

export default FormTask;
