import React from "react";

const List = (props) => {
  return (
    <>
      <ul className="user-list">
        {props.data.map((task) => (
          <li key={task.id}>
            {" "}
            <Tasks {...task} removeItem={props.removeItem} />
          </li>
        ))}
      </ul>
    </>
  );
};

const Tasks = ({ id, title, description, removeItem }) => {
  return (
    <article>
      <div className="person-info">
        <div className="person-action">
          <h4>{title}</h4>
          <button className="btn" onClick={() => removeItem(id)}>
            {"Done"}
          </button>
        </div>
        <p>{description}</p>
      </div>
    </article>
  );
};

const FormTask = (insertItem) => {
  return (
      <>
          <form action="">
              <div className="row">
                  <label htmlFor="taskTitle">Task title</label>
                  <input type="text" name="taskTitle" id="taskTitle" />
              </div>
              <div className="row">
                  <label htmlFor="taskDescription">Task description</label>
                  <input type="text" name="taskDescription" id="taskDescription" />
              </div>
              <button className="btn" onClick={() => insertItem()}>{"Add task"}</button>
          </form>
      </>
  );
  };


export default List;
