import List from "./List";
import data from "./data";
import { useState } from "react";
import FormTask from "./form";


function App() {
  let [task, setTask] = useState(data);

  let newItem =  {
    id: 6,
    title: "Mail urgente",
    description: "Inviare urgente al capo",
  };

  const insertItem = () => {
    setTask((oldValue)=>oldValue.concat({newItem}));
  };

  const removeItem = (id) => {
    setTask((oldValue) => oldValue.filter((value) => value.id !== id));
  };

  const reloadAllItem = () => {
    setTask(data);
  };

  return (
    <>
      <section>
        <div className="container">
          <h2 style={{ color: "var(--bg-blue)" }}>To do list</h2>

          <FormTask insertItem={insertItem} />
          <button className="btn" onClick={() => insertItem(newItem)}>{"Add task"}</button>

          <div className="people-list">
            <List data={task} removeItem={removeItem} />
          </div>
          <div className="btn-group">
            <button className="btn btn-reset" onClick={reloadAllItem}>
              {" "}
              Reload default task{" "}
            </button>
            <button className="btn btn-delete" onClick={() => setTask([])}>
              {" "}
              All done
            </button>
          </div>
        </div>
      </section>
    </>
  );
}

export default App;
